mapper.select:
		select a.`id`,b.`name`,a.`user_id`,c.`role_name`,b.`organization_name` as org_name,b.`idcd_no`,b.`mac_adress`,b.`wireless_mac_adress` as w_mac_adress,b.`mobile`,a.`session_id`,a.`operation`,a.`user_ip`,a.`create_time`,d.`out_time`,(d.`out_time`-a.create_time) as time from crmkhv2_login_log a LEFT JOIN bootdo_sys_user b on a.user_id=b.username LEFT JOIN bootdo_sys_role c on a.user_id=c.role_sign LEFT JOIN crmkhv2_out_log d on a.session_id=d.session_id

		   and a.id = #{id} 
		   and a.user_id = #{userId} 
		   and a.session_id like '%${sessionId}%' 
		   and a.operation like '%${operation}%' 
		   and b.mobile = #{mobile} 
		   and a.user_ip = #{userIp} 
		   and a.create_time >= date_format (#{startTime},'%Y-%m-%d 00:00:00') and create_time <= date_format (#{endTime},'%Y-%m-%d 23:59:59')



                order by #{sort} ,#{order}


                order by a.id asc



			limit #{offset}, #{limit}

		select (@i :=@i + 1) AS `id`,b.`name`,a.`user_id`,c.`role_name`,b.`organization_name` as org_name,b.`idcd_no`,b.`mac_adress`,b.`wireless_mac_adress` as w_mac_adress,b.`mobile`,a.`session_id`,a.`operation`,a.`user_ip`,a.`create_time`,d.`out_time`,(d.`out_time`-a.create_time) as time,(CASE WHEN e.`alarm_type` is NULL THEN '否' ELSE '是' END) AS police,e.`alarm_type` AS police_typ from crmkhv2_login_log a LEFT JOIN crmkhv2_login_user b on a.user_id=b.username LEFT JOIN bootdo_sys_role c on a.user_id=c.role_sign LEFT JOIN crmkhv2_out_log d on a.session_id=d.session_id LEFT JOIN crmkhv2_alarm e ON a.session_id=e.session_id,(SELECT @i := 0) AS it

		   and a.id = #{id} 
		   and a.user_id = #{userId} 
		   and a.session_id = #{sessionId} 
		   and a.operation = #{operation} 
		   and a.user_ip = #{userIp} 
		   and b.mobile = #{mobile} 
		   and e.`alarm_type` is not null
		   and e.`alarm_type` is null
		   and a.create_time >= date_format (#{startTime},'%Y-%m-%d 00:00:00') and create_time <= date_format (#{endTime},'%Y-%m-%d 23:59:59')



                order by #{sort} ,#{order}


                order by a.create_time desc



			limit #{offset}, #{limit}

select count(*) from crmkhv2_login_log a LEFT JOIN crmkhv2_login_user b on a.user_id=b.username LEFT JOIN bootdo_sys_role c on a.user_id=c.role_sign LEFT JOIN crmkhv2_out_log d on a.session_id=d.session_id LEFT JOIN crmkhv2_alarm e ON a.session_id=e.session_id

	 and a.id = #{id} 
	 and a.user_id = #{userId} 
	 and a.session_id = #{sessionId} 
	 and a.operation = #{operation} 
	 and a.user_ip = #{userIp} 
	 and b.mobile = #{mobile} 
	 and e.`alarm_type` is not null
	 and e.`alarm_type` is null
	 and a.create_time >= date_format (#{startTime},'%Y-%m-%d 00:00:00') and create_time <= date_format (#{endTime},'%Y-%m-%d 23:59:59')
