select ProvinceID from crmkhv2_regional_basic_information
date_format (#{endDate},'%Y-%m-%d') BETWEEN start_date AND end_date and ProvinceName = #{provinceName}
group by ProvinceID

select count(1) from (select count(1) from tmpv2_cust_od_sum a LEFT JOIN ydserver.gs gs ON a.branch_code = gs.bm LEFT JOIN ydserver.gs yjwd ON a.gs = yjwd.bm LEFT JOIN crmkhv2_regional_basic_information t4 ON gs.szd = t4.CountyID and date_format (#{end_date},'%Y-%m-%d') BETWEEN t4.start_date AND t4.end_date LEFT JOIN tmpv2_cust_price_stats b ON b.customer_id = a.customer_id AND a.customer_source_type = b.customer_source_type AND a.gs = b.gs WHERE 1=1 and a.branch_code = #{branch_code} and a.customer_id = #{customer_id} and a.customer_name = #{customer_name} and b.qu_date BETWEEN date_format ( #{start_date}, '%Y-%m-%d' ) AND date_format ( #{end_date}, '%Y-%m-%d' ) and a.qu_date BETWEEN date_format ( #{start_date}, '%Y-%m-%d' ) AND date_format ( #{end_date}, '%Y-%m-%d' )
group by a.customer_id,a.customer_source_type,b.customer_id,b.customer_source_type
)as a

select count(1) from (select count(1) from tmpv2_cust_od_sum a LEFT JOIN ydserver.gs gs ON a.branch_code = gs.bm LEFT JOIN ydserver.gs yjwd ON a.gs = yjwd.bm LEFT JOIN crmkhv2_regional_basic_information t4 ON gs.szd = t4.CountyID and date_format (#{end_date},'%Y-%m-%d') BETWEEN t4.start_date AND t4.end_date LEFT JOIN tmpv2_cust_price_stats_sjd2 b ON b.customer_id = a.customer_id AND a.customer_source_type = b.customer_source_type AND a.gs = b.gs WHERE 1=1 and a.branch_code = #{branch_code} and a.customer_id = #{customer_id} and a.customer_name = #{customer_name} and b.start_date = date_format ( #{start_date}, '%Y-%m-%d' ) AND b.end_date = date_format ( #{end_date}, '%Y-%m-%d' ) and a.qu_date BETWEEN date_format ( #{start_date}, '%Y-%m-%d' ) AND date_format ( #{end_date}, '%Y-%m-%d' )
group by a.customer_id,a.customer_source_type,b.customer_id,b.customer_source_type
)as a

