SELECT c.*,d.* from ( select a.provinceid as provinceid ,a.ProvinceName as ProvinceName,a.bigarea as bigarea , sum(order_sum) as order_sum, sum(order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as order_avg, sum(ordinary_order_sum) as ordinary_order_sum, sum(customer_sum) as customer_sum, sum(dianzi_order_sum) as dianzi_order_sum, sum(dianzi_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as dianzi_order_sum_avg, sum(a_customer_sum) as a_customer_sum,sum(b_customer_sum) as b_customer_sum,sum(c_customer_sum) as c_customer_sum , sum(d_customer_sum) as d_customer_sum,sum(e_customer_sum) as e_customer_sum,sum(f_customer_sum) as f_customer_sum , sum(g_customer_sum) as g_customer_sum, sum(a_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as a_order_avg , sum(b_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as b_order_avg , sum(c_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as c_order_avg , sum(d_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as d_order_avg , sum(e_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as e_order_avg , sum(f_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as f_order_avg , sum(g_order_sum)/(DATEDIFF(#{endDate}, #{startDate})+1) as g_order_avg , sum(a_order_sum) as a_order_sum ,sum(b_order_sum) as b_order_sum ,sum(c_order_sum) as c_order_sum , sum(d_order_sum) as d_order_sum ,sum(e_order_sum) as e_order_sum ,sum(f_order_sum) as f_order_sum , sum(g_order_sum) as g_order_sum
from tmpv2_province_od_sum_sjd a where 1=1
and a.bigArea = #{bigarea}
and a.ProvinceID in
${TProvinceID}
and a.provinceid is not null and a.start_date = date_format (#{startDate},'%Y-%m-%d') and a.end_date = date_format (#{endDate},'%Y-%m-%d') group by a.provinceid ) c left join ( select b.provinceid, cast(sum(ifnull(a_price_sum,0)) as decimal(10,2)) a_price_sum,cast(sum(ifnull(b_price_sum,0)) as decimal(10,2)) b_price_sum, cast(sum(ifnull(c_price_sum,0)) as decimal(10,2)) c_price_sum,cast(sum(ifnull(d_price_sum,0)) as decimal(10,2)) d_price_sum, cast(sum(ifnull(e_price_sum,0)) as decimal(10,2)) e_price_sum,cast(sum(ifnull(f_price_sum,0)) as decimal(10,2)) f_price_sum, cast(sum(ifnull(g_price_sum,0)) as decimal(10,2)) g_price_sum, sum(IFNULL(a_all_price_sum,0)) a_all_price_sum,sum(IFNULL(b_all_price_sum,0)) b_all_price_sum, sum(IFNULL(c_all_price_sum,0)) c_all_price_sum,sum(IFNULL(d_all_price_sum,0)) d_all_price_sum, sum(IFNULL(e_all_price_sum,0)) e_all_price_sum,sum(IFNULL(f_all_price_sum,0)) f_all_price_sum, sum(IFNULL(g_all_price_sum,0)) g_all_price_sum ,sum(IFNULL(all_price_sum,0)) dianziPriceSum, sum(IFNULL(all_price_sum,0))/(DATEDIFF(#{endDate}, #{startDate})+1) as dianzi_price_sum_avg from tmpv2_province_price_sum_sjd b where 1=1
and b.bigArea = #{bigarea}
and b.ProvinceID in
${tProvinceID}
and b.provinceid is not null and b.start_date = date_format (#{startDate},'%Y-%m-%d') and b.end_date =date_format (#{endDate},'%Y-%m-%d') group by b.provinceid ) d on c.provinceid=d.provinceid

