from tmpv2_province_od_sum_sjd a where 1=1
and a.ProvinceID in
${TProvinceIDs}
and a.ProvinceID in
${TProvinceIDs}
and a.start_date = date_format (#{startDate},'%Y-%m-%d') and a.end_date = date_format (#{endDate},'%Y-%m-%d') group by a.provinceid ) c left join ( select b.provinceid, cast(sum(ifnull(a_price_sum,0)) as decimal(10,2)) a_price_sum,cast(sum(ifnull(b_price_sum,0)) as decimal(10,2)) b_price_sum, cast(sum(ifnull(c_price_sum,0)) as decimal(10,2)) c_price_sum,cast(sum(ifnull(d_price_sum,0)) as decimal(10,2)) d_price_sum, cast(sum(ifnull(e_price_sum,0)) as decimal(10,2)) e_price_sum,cast(sum(ifnull(f_price_sum,0)) as decimal(10,2)) f_price_sum, cast(sum(ifnull(g_price_sum,0)) as decimal(10,2)) g_price_sum, sum(IFNULL(a_all_price_sum,0)) a_all_price_sum,sum(IFNULL(b_all_price_sum,0)) b_all_price_sum, sum(IFNULL(c_all_price_sum,0)) c_all_price_sum,sum(IFNULL(d_all_price_sum,0)) d_all_price_sum, sum(IFNULL(e_all_price_sum,0)) e_all_price_sum,sum(IFNULL(f_all_price_sum,0)) f_all_price_sum, sum(IFNULL(g_all_price_sum,0)) g_all_price_sum ,sum(IFNULL(all_price_sum,0)) dianziPriceSum, sum(IFNULL(all_price_sum,0))/(DATEDIFF(#{endDate}, #{startDate})+1) as dianzi_price_sum_avg from tmpv2_province_price_sum_sjd b where 1=1
