select IFNULL(sum(base4.order_sum) ,0) order_sum, IFNULL(sum(base4.order_sum) ,0)/(DATEDIFF(#{endDate}, #{startDate})+1) order_avg, IFNULL(sum(base4.ordinary_order_sum) ,0) ordinary_order_sum, IFNULL(sum(base4.dianzi_order_sum),0) dianzi_order_sum, IFNULL(sum(base4.customer_sum),0) customer_sum, IFNULL(sum(base4.dianzi_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) as dianzi_order_sum_avg, IFNULL(sum(base4.a_customer_sum),0) a_customer_sum, IFNULL(sum(base4.b_customer_sum),0) b_customer_sum, IFNULL(sum(base4.c_customer_sum),0) c_customer_sum, IFNULL(sum(base4.d_customer_sum),0) d_customer_sum, IFNULL(sum(base4.e_customer_sum),0) e_customer_sum, IFNULL(sum(base4.f_customer_sum),0) f_customer_sum, IFNULL(sum(base4.g_customer_sum),0) g_customer_sum,
IFNULL(sum(base4.a_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) a_order_avg, IFNULL(sum(base4.b_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) b_order_avg, IFNULL(sum(base4.c_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) c_order_avg, IFNULL(sum(base4.d_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) d_order_avg, IFNULL(sum(base4.e_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) e_order_avg, IFNULL(sum(base4.f_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) f_order_avg, IFNULL(sum(base4.g_order_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) g_order_avg,
IFNULL(sum(base4.a_order_sum),0) a_order_sum, IFNULL(sum(base4.b_order_sum),0) b_order_sum, IFNULL(sum(base4.c_order_sum),0) c_order_sum, IFNULL(sum(base4.d_order_sum),0) d_order_sum, IFNULL(sum(base4.e_order_sum),0) e_order_sum, IFNULL(sum(base4.f_order_sum),0) f_order_sum, IFNULL(sum(base4.g_order_sum),0) g_order_sum,
IFNULL(sum(base4.a_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) a_price_sum, IFNULL(sum(base4.b_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) b_price_sum, IFNULL(sum(base4.c_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) c_price_sum, IFNULL(sum(base4.d_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) d_price_sum, IFNULL(sum(base4.e_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) e_price_sum, IFNULL(sum(base4.f_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) f_price_sum, IFNULL(sum(base4.g_all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) g_price_sum,
IFNULL(sum(base4.a_all_price_sum),0) a_all_price_sum, IFNULL(sum(base4.b_all_price_sum),0) b_all_price_sum, IFNULL(sum(base4.c_all_price_sum),0) c_all_price_sum, IFNULL(sum(base4.d_all_price_sum),0) d_all_price_sum, IFNULL(sum(base4.e_all_price_sum),0) e_all_price_sum, IFNULL(sum(base4.f_all_price_sum),0) f_all_price_sum, IFNULL(sum(base4.g_all_price_sum),0) g_all_price_sum, IFNULL(sum(base4.all_price_sum),0) dianziPriceSum, IFNULL(sum(base4.all_price_sum),0)/(DATEDIFF(#{endDate}, #{startDate})+1) as dianzi_price_sum_avg from (
