from ( select a.customer_id,a.start_date,a.end_date,a.customer_name,a.branch_code,a.gs,a.order_sum, a.order_avg,a.customer_source_type,a.price_level, CAST( a.order_avg*b.price AS DECIMAL(8,2)) price_num, CAST( a.order_sum*b.price AS DECIMAL(8,2)) all_price_sum from ( SELECT g.*, bp.ProvinceID, case when bp.ProvinceID in (${firstProvinceids}) then 'X' when bp.ProvinceID is null then 'X' else 'Y' end as price_type from ( select a.customer_id, #{startDate} start_date, #{endDate} end_date, a.customer_name, a.branch_code, a.gs, sum(a.daily_order_num) order_sum, sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) order_avg, a.customer_source_type, case when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) <= 50 then 'a' when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) > 50 and sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) <= 100 then 'b' when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) > 100 and sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) <= 500 then 'c' when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) > 500 and sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) <= 1000 then 'd' when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) > 1000 and sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) <= 3000 then 'e' when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) > 3000 and sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) <= 5000 then 'f' when sum(a.daily_order_num)/(datediff(#{endDate},#{startDate})+1) > 5000 then 'g' else '' end as price_level from crmkhv2_report_order_stats_all a where a.qu_date BETWEEN date_format ( #{startDate}, '%Y-%m-%d' ) AND date_format ( #{endDate}, '%Y-%m-%d' ) GROUP BY customer_id,branch_code,customer_source_type,gs ) g left join ( SELECT a.branch_code, b.ProvinceID from crmkhv2_report_order_stats_all a LEFT JOIN ydserver.gs gs on a.branch_code = gs.bm LEFT JOIN crmkhv2_regional_basic_information b ON gs.szd = b.CountyID and date_format (#{endDate},'%Y-%m-%d') BETWEEN b.start_date AND b.end_date where a.qu_date BETWEEN date_format ( #{startDate}, '%Y-%m-%d' ) AND date_format ( #{endDate}, '%Y-%m-%d' ) GROUP BY a.branch_code,b.ProvinceID ) bp on g.branch_code = bp.branch_code GROUP BY customer_id,customer_name,branch_code,customer_source_type,gs
)a LEFT JOIN crmkhv2_config_finance_price_table b on a.price_level = b.price_level and a.price_type = b.price_type order by a.customer_id ) a LEFT JOIN ydserver.gs gs ON a.branch_code = gs.bm LEFT JOIN ydserver.gs yjwd ON a.gs = yjwd.bm LEFT JOIN crmkhv2_regional_basic_information t4 ON gs.szd = t4.CountyID and date_format (#{endDate},'%Y-%m-%d') BETWEEN t4.start_date AND t4.end_date WHERE 1=1 and a.customer_id = #{customerId}
and a.ProvinceID in
${provinceIds}
and a.ProvinceID in
${provinceIds}
and a.branch_code = #{branchCode}
group by a.customer_id,a.customer_source_type,a.branch_code,a.gs order by a.customer_id
limit #{offset}, #{limit}

