formation b ON gs.szd = b.CountyID and date_format (#{endDate},'%Y-%m-%d') BETWEEN b.start_date AND b.end_date where a.qu_date BETWEEN date_format ( #{startDate}, '%Y-%m-%d' ) AND date_format ( #{endDate}, '%Y-%m-%d' ) GROUP BY a.branch_code,b.ProvinceID ) bp on g.branch_code = bp.branch_code GROUP BY customer_id,customer_name,branch_code,customer_source_type,gs
)a LEFT JOIN crmkhv2_config_finance_price_table b on a.price_level = b.price_level and a.price_type = b.price_type order by a.customer_id ) a LEFT JOIN ydserver.gs gs ON a.branch_code = gs.bm LEFT JOIN ydserver.gs yjwd ON a.gs = yjwd.bm LEFT JOIN crmkhv2_regional_basic_information t4 ON gs.szd = t4.CountyID and date_format (#{endDate},'%Y-%m-%d') BETWEEN t4.start_date AND t4.end_date WHERE 1=1 and a.customer_id = #{customerId}
