select sum(a.order_sum) order_sum, cast(sum(a.order_sum) as decimal(10,0)) order_avg, '-' customer_name,a.customer_id,a.price_level,t4.bigarea,t4.ProvinceName,t4.CityName,gs.mc branch_name, a.branch_code branch_code,yjwd.mc yjmc,a.gs yjbm, cast(sum(ifnull(b.all_price_sum,0)) as decimal(10,2)) dianziPriceSumAvg, cast(sum(ifnull(b.all_price_sum,0)) as decimal(10,2)) dianziPriceSum, CASE a.customer_source_type WHEN 1 THEN '' WHEN 2 THEN '' WHEN 4 THEN '' ELSE '' END customer_source_type from tmpv2_cust_od_sum a LEFT JOIN ydserver.gs gs ON a.branch_code = gs.bm LEFT JOIN ydserver.gs yjwd ON a.gs = yjwd.bm LEFT JOIN crmkhv2_regional_basic_information t4 ON gs.szd = t4.CountyID and date_format (#{end_date},'%Y-%m-%d') BETWEEN t4.start_date AND t4.end_date LEFT JOIN tmpv2_cust_price_sum b ON b.customer_id = a.customer_id AND a.customer_source_type = b.customer_source_type AND a.gs = b.gs WHERE 1=1 and a.branch_code = #{branch_code} and a.customer_id = #{customer_id} and a.customer_name = #{customer_name} and b.qu_date BETWEEN date_format ( #{start_date}, '%Y-%m-%d' ) AND date_format ( #{end_date}, '%Y-%m-%d' ) and a.qu_date BETWEEN date_format ( #{start_date}, '%Y-%m-%d' ) AND date_format ( #{end_date}, '%Y-%m-%d' )
group by a.customer_id,a.customer_source_type,b.customer_id,b.customer_source_type
limit #{offset}, #{limit}

select sum(a.order_sum) order_sum, cast(sum(a.order_sum)/(DATEDIFF(#{end_date}, #{start_date})+1) as decimal(10,2)) order_avg, '-' customer_name, a.customer_id, cast(sum(ifnull(b.all_price_sum,0))/(DATEDIFF(#{end_date}, #{start_date})+1) as decimal(10,2)) dianzi_order_sum_avg, cast(sum(ifnull(b.all_price_sum,0)) as decimal(10,2)) dianzi_order_sum, t4.bigarea,t4.ProvinceName,t4.CityName, gs.mc branch_name, a.branch_code branch_code, yjwd.mc yjmc, a.gs yjbm, a.price_level, CASE a.customer_source_type WHEN 1 THEN '' WHEN 2 THEN '' WHEN 4 THEN '' ELSE '' END customer_source_type from tmpv2_cust_od_sum_sjd a LEFT JOIN ydserver.gs gs ON a.branch_code = gs.bm LEFT JOIN ydserver.gs yjwd ON a.gs = yjwd.bm LEFT JOIN crmkhv2_regional_basic_information t4 ON gs.szd = t4.CountyID and date_format (#{end_date},'%Y-%m-%d') BETWEEN t4.start_date AND t4.end_date LEFT JOIN tmpv2_cust_price_sum_sjd b ON b.customer_id = a.customer_id AND a.customer_source_type = b.customer_source_type AND a.gs = b.gs WHERE 1=1 and a.branch_code = #{branch_code} and a.customer_id = #{customer_id} and a.customer_name = #{customer_name} and a.start_date = date_format (#{start_date},'%Y-%m-%d') and a.end_date = date_format (#{end_date},'%Y-%m-%d') and b.start_date = date_format (#{start_date},'%Y-%m-%d') and b.end_date = date_format (#{end_date},'%Y-%m-%d')
group by a.customer_id,a.customer_source_type,b.customer_id,b.customer_source_type
limit #{offset}, #{limit}

