insert into tmpv2_gs_price_sum_sjd2 (`branch_code`,`start_date`,`end_date`,`price_sum`,`all_price_sum`,`a_price_sum`,`b_price_sum`,`c_price_sum`,`d_price_sum`,`e_price_sum`,`f_price_sum`,`g_price_sum`,`a_all_price_sum`,`b_all_price_sum`,`c_all_price_sum`,`d_all_price_sum`,`e_all_price_sum`,`f_all_price_sum`,`g_all_price_sum`) SELECT branch_code, start_date,end_date, SUM(price_sum) price_sum, SUM(all_price_sum) all_price_sum, SUM(a_price_sum) a_price_sum, SUM(b_price_sum) b_price_sum, SUM(c_price_sum) c_price_sum, SUM(d_price_sum) d_price_sum, SUM(e_price_sum) e_price_sum, SUM(f_price_sum) f_price_sum, SUM(g_price_sum) g_price_sum, SUM(a_all_price_sum) a_all_price_sum, SUM(b_all_price_sum) b_all_price_sum, SUM(c_all_price_sum) c_all_price_sum, SUM(d_all_price_sum) d_all_price_sum, SUM(e_all_price_sum) e_all_price_sum, SUM(f_all_price_sum) f_all_price_sum, SUM(g_all_price_sum) g_all_price_sum FROM(SELECT * FROM tmpv2_gs_price_sum_sjd where 1=1 and
(start_date = date_format ( #{item.yone}, '%Y-%m-%d' ) AND end_date = date_format ( #{item.ytwo}, '%Y-%m-%d' ))
)he GROUP BY branch_code

insert into tmpv2_bigarea_price_sum_sjd2 (`bigarea`,`start_date`,`end_date`,`all_price_sum`,`a_price_sum`,`b_price_sum`,`c_price_sum`,`d_price_sum`,`e_price_sum`,`f_price_sum`,`g_price_sum`,`a_all_price_sum`,`b_all_price_sum`,`c_all_price_sum`,`d_all_price_sum`,`e_all_price_sum`,`f_all_price_sum`,`g_all_price_sum`) SELECT bigarea, start_date, end_date, SUM(a_price_sum) a_price_sum, SUM(b_price_sum) b_price_sum, SUM(c_price_sum) c_price_sum, SUM(d_price_sum) d_price_sum, SUM(e_price_sum) e_price_sum, SUM(f_price_sum) f_price_sum, SUM(g_price_sum) g_price_sum, sum(all_price_sum) all_price_sum, SUM(a_all_price_sum) a_all_price_sum, SUM(b_all_price_sum) b_all_price_sum, SUM(c_all_price_sum) c_all_price_sum, SUM(d_all_price_sum) d_all_price_sum, SUM(e_all_price_sum) e_all_price_sum, SUM(f_all_price_sum) f_all_price_sum, SUM(g_all_price_sum) g_all_price_sum FROM(SELECT * FROM tmpv2_bigarea_price_sum_sjd where 1=1 and
(start_date = date_format ( #{item.yone}, '%Y-%m-%d' ) AND end_date = date_format ( #{item.ytwo}, '%Y-%m-%d' ))
)he GROUP BY bigarea

insert into tmpv2_all_price_sum_sjd2 (`start_date`,`end_date`,`a_price_sum`,`b_price_sum`,`c_price_sum`,`d_price_sum`,`e_price_sum`,`f_price_sum`,`g_price_sum`,`all_price_sum`,`a_all_price_sum`,`b_all_price_sum`,`c_all_price_sum`,`d_all_price_sum`,`e_all_price_sum`,`f_all_price_sum`,`g_all_price_sum`) select start_date,end_date, sum(a_price_sum) a_price_sum, sum(b_price_sum) b_price_sum, sum(c_price_sum) c_price_sum, sum(d_price_sum) d_price_sum, sum(e_price_sum) e_price_sum, sum(f_price_sum) f_price_sum, sum(g_price_sum) g_price_sum, sum(all_price_sum) all_price_sum, sum(a_all_price_sum) a_all_price_sum, sum(b_all_price_sum) b_all_price_sum, sum(c_all_price_sum) c_all_price_sum, sum(d_all_price_sum) d_all_price_sum, sum(e_all_price_sum) e_all_price_sum, sum(f_all_price_sum) f_all_price_sum, sum(g_all_price_sum) g_all_price_sum from (select * from tmpv2_all_price_sum_sjd where 1=1 and
(start_date = date_format ( #{item.yone}, '%Y-%m-%d' ) AND end_date = date_format ( #{item.ytwo}, '%Y-%m-%d' ))
) h


