mapper.select:
		select `session_id`,`user_name`,`alarm_type`,`time` from crmkhv2_alarm

		  		   and session_id = #{sessionId} 
		  		   and user_name = #{userName} 
		  		   and alarm_type = #{alarmType} 
		  		   and time = #{time} 



                order by #{sort}, #{order}


                order by session_id desc



			limit #{offset}, #{limit}

select count(*) from crmkhv2_alarm

  		   and session_id = #{sessionId} 
  		   and user_name = #{userName} 
  		   and alarm_type = #{alarmType} 
  		   and time = #{time}
